    public function testUtf8Strstr()
    {
        $tests = [
            'ABC@ä¸­æ–‡ç©ºç™½.com' => [
                'ABC',
                '@ä¸­æ–‡ç©ºç™½.com',
            ],
            ' @ - Ã–Ã„Ãœ- ' => [
                ' ',
                '@ - Ã–Ã„Ãœ- ',
            ],
            'Ã¶Ã¤@Ã¼' => [
                'Ã¶Ã¤',
                '@Ã¼',
            ],
            '' => [
                false,
                false,
            ],
            '  ' => [
                false,
                false,
            ],
        ];

        foreach ($tests as $before => $after) {
            static::assertSame($after[0], UTF8::strstr($before, '@', true), 'tested: ' . $before);
            // alias
            static::assertSame($after[0], UTF8::strchr($before, '@', true), 'tested: ' . $before);
        }

        // ---

        foreach ($tests as $before => $after) {
            static::assertSame($after[1], UTF8::strstr($before, '@'), 'tested: ' . $before);
        }

        // --- UTF-8

        static::assertSame('ABC', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', '@', true, 'UTF-8'));
        static::assertSame('@ä¸­æ–‡ç©ºç™½.com', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', '@', false, 'UTF-8'));

        static::assertSame('ABC@', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'ä¸­æ–‡ç©ºç™½', true, 'UTF-8'));
        static::assertSame('ä¸­æ–‡ç©ºç™½.com', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'ä¸­æ–‡ç©ºç™½', false, 'UTF-8'));

        // --- ISO

        static::assertSame('ABC', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', '@', true, 'ISO'));
        static::assertSame('@ä¸­æ–‡ç©ºç™½.com', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', '@', false, 'ISO'));

        static::assertSame('ABC@', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'ä¸­æ–‡ç©ºç™½', true, 'ISO'));
        static::assertSame('ä¸­æ–‡ç©ºç™½.com', UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'ä¸­æ–‡ç©ºç™½', false, 'ISO'));

        // --- false

        static::assertFalse(UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'z', true, 'UTF-8'));
        static::assertFalse(UTF8::strstr('ABC@ä¸­æ–‡ç©ºç™½.com', 'z', false, 'UTF-8'));
        static::assertFalse(UTF8::strstr('', 'z', true, 'UTF-8'));
        static::assertFalse(UTF8::strstr('', 'z', false, 'UTF-8'));
    }

