    {
        $dirtyTestString = "\xEF\xBB\xBFâ€ž Abc d\00e\00f\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ";

        static::assertSame("\xEF\xBB\xBFâ€ž Abc def\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString));
        static::assertSame("\xEF\xBB\xBFâ€ž Abc def \x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, true, false, false));
        static::assertSame("\xEF\xBB\xBFâ€ž Abc def\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, false, false, true));
        static::assertSame("\xEF\xBB\xBFâ€ž Abc def\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, false, false, false));
        static::assertSame("\xEF\xBB\xBF\" Abc def\xc2\xa0\x20...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, false, true, true));
        static::assertSame("\xEF\xBB\xBF\" Abc def\xc2\xa0\x20...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, false, true, false));
        static::assertSame("\xEF\xBB\xBF\" Abc def  ...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, true, true, false));
        static::assertSame("\xEF\xBB\xBF\" Abc def\xc2\xa0\x20...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, false, true, true, true));
        static::assertSame("â€ž Abc def\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString, true, false, false, false));
        static::assertSame("â€ž Abc def\xc2\xa0\x20â€¦â€ â€” ðŸ˜ƒ", UTF8::clean($dirtyTestString, true, false, false, true));
        static::assertSame("\" Abc def\xc2\xa0\x20...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, true, false, true, false));
        static::assertSame("\" Abc def\xc2\xa0\x20...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, true, false, true, true));
        static::assertSame('â€ž Abc def  â€¦â€ â€” ðŸ˜ƒ', UTF8::clean($dirtyTestString, true, true, false, false));
        static::assertSame('â€ž Abc def' . \html_entity_decode('&nbsp;') . ' â€¦â€ â€” ðŸ˜ƒ', UTF8::clean($dirtyTestString, true, true, false, true));
        static::assertSame('" Abc def  ..." - ðŸ˜ƒ', UTF8::clean($dirtyTestString, true, true, true, false));
        static::assertSame("\" Abc def\xc2\xa0 ...\" - ðŸ˜ƒ", UTF8::clean($dirtyTestString, true, true, true, true));
    }

